import React, { useState } from 'react';
import { BookOutlined, UserOutlined, ShopOutlined, FormOutlined, CopyOutlined, PieChartOutlined, AppstoreOutlined } from '@ant-design/icons';
import type { MenuProps } from 'antd';
import { Layout, Menu, theme } from 'antd';
import { NavLink } from 'react-router-dom';

const { Content, Sider } = Layout;

type MenuItem = Required<MenuProps>['items'][number];

function getItem(label: React.ReactNode, key: React.Key, icon?: React.ReactNode, children?: MenuItem[]): MenuItem {
    return { key, icon, children, label } as MenuItem;
}

const items: MenuItem[] = [
    getItem('Dashboard', '1', < NavLink to="/admin/dashboard"><PieChartOutlined /></NavLink>),
    getItem('Product', '2', <AppstoreOutlined />, [
        getItem('Chi tiết', '3', <NavLink to="/admin/product"><CopyOutlined /></NavLink>),
        getItem('Thêm mới', '4', <NavLink to="/admin/product/create"><FormOutlined /></NavLink>),
    ]),
    getItem('Category', '5', <BookOutlined />, [
        getItem('Chi tiết', '6', <NavLink to="/admin/category"><CopyOutlined /></NavLink>),
    ]),
    getItem('Order', '7', <NavLink to="/admin/order"><ShopOutlined /></NavLink>),
    getItem('User', '8', <NavLink to="/admin/user"><UserOutlined /></NavLink>)
];

interface SideBarAdminProps {
    header: React.ReactNode;
    content: React.ReactNode;
    footer: React.ReactNode;
}

export const SideBarAdmin: React.FC<SideBarAdminProps> = ({ header, content, footer }: SideBarAdminProps) => {
    const [collapsed, setCollapsed] = useState(false);
    const {
        token: { colorBgContainer },
    } = theme.useToken();

    return (
        <Layout style={{ minHeight: '100vh' }}>
            <Sider collapsible collapsed={collapsed} onCollapse={(value) => setCollapsed(value)} >
                <svg width="200" className='m-3' height="60" xmlns="http://www.w3.org/2000/svg" href="http://www.w3.org/1999/xlink" viewBox="0 0 269.231 50"><defs><pattern id="patternLogo" preserveAspectRatio="xMidYMid slice" width="100%" height="100%" viewBox="0 0 530 95"><image width="480" height="100" href="data:image/png;base64,"></image></pattern></defs> <rect id="header__logo--bg" width="269.231" height="50" fill="url(#patternLogo)"></rect></svg>
                <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline" items={items} />
            </Sider>

            <Layout className="site-layout">

                {header}

                <Content>
                    <div style={{ margin: '16px 16px', padding: 24, minHeight: 360, background: colorBgContainer }}>
                        {content}
                    </div>
                </Content>

                {footer}

            </Layout>
        </Layout>
    );
};